function findSceneEntries(obj) {
  const scenes = [];

  if (!obj || typeof obj !== 'object') {
    return scenes;
  }

  for (const [key, value] of Object.entries(obj)) {
    if (key.toLowerCase().startsWith("scene") && typeof value === "string") {
      scenes.push(value);
    } else if (typeof value === "object" && value !== null) {
      scenes.push(...findSceneEntries(value));
    }
  }

  return scenes;
}

try {
  const inputData = $json;  // ‚úÖ Changed from items[0].json
  const scenes = findSceneEntries(inputData);

  if (scenes.length === 0) {
    return {
      json: {
        warning: "No scene keys found at any level.",
        scene_count: 0,
        scenes: []
      }
    };
  }

  return {
    json: {
      success: true,
      scene_count: scenes.length,
      scenes: scenes.map((scene, index) => ({
        index: index + 1,
        description: scene
      }))
    }
  };

} catch (e) {
  return {
    json: {
      error: true,
      message: "Could not extract scenes properly",
      details: e.message
    }
  };
}

