// Prepare data for Google Sheet with file info
const idea = $('Prompts AI Agent').item.json.output?.Idea || 'No idea';
const caption = $('2. Enrich Idea into Plan').item.json.output?.Caption || 'No caption';
const environment = $('Prompts AI Agent').item.json.output?.Environment || 'No environment';
const sound = $('Prompts AI Agent').item.json.output?.Sound || 'No sound';

// Generate filename with timestamp
const now = new Date();
const timestamp = now.toISOString().replace(/[-:]/g, '').replace('T', '_').split('.')[0];
const fileName = `video_${timestamp}.mp4`;
const filePath = `/tmp/videos/${fileName}`;

// Get file size from binary data (before it's written)
const fileSize = $input.first().binary?.data?.data?.length || 0;
const fileSizeMB = (fileSize / 1024 / 1024).toFixed(2);

return {
  json: {
    file_name: fileName,
    file_path: filePath,
    file_size_mb: fileSizeMB,
    idea: idea,
    caption: caption,
    environment: environment,
    sound: sound,
    status: 'video_saved',
    created_at: now.toISOString(),
    mime_type: 'video/mp4',
    url: filePath
  },
  // IMPORTANT: Pass through binary data for Upload to YouTube
  binary: $input.first().binary
};
